import sys
from classifier import *

if len(sys.argv) < 3:
    sys.exit('Usage: python test.py [train mode: 0/1] [name: zhai/kevin/kevin/hahn/yoon/knox/sath/rhine]')

# Generate the training sample again. If features are changed in feature.py, need to regenerate the sample again
if int(sys.argv[1]) == 1:
    observation_sequences_entire1, label_sequences_entire1 = get_training_samples('http://scholar.google.com/citations?user=Kv9AbjMAAAAJ&hl=en')      # large training sample
    observation_sequences_entire2, label_sequences_entire2 = get_training_samples('http://scholar.google.com/citations?user=YU-baPIAAAAJ&hl=en')    # small training sample
    observation_sequences_entire = observation_sequences_entire1 + observation_sequences_entire2
    label_sequences_entire = label_sequences_entire1 + label_sequences_entire2

    # Serializing training sets
    log_err('Saving training set...')
    fp = open('training_observation.pkl', 'wb')
    pickle.dump(observation_sequences_entire, fp, -1)
    fp.close()
    fp = open('training_labels.pkl', 'wb')
    pickle.dump(label_sequences_entire, fp, -1)
    fp.close()

# Use the serialized training sample
if int(sys.argv[1]) == 0:
    # Pickle load samples
    observation_sequences_entire = pickle.load(open('training_observation.pkl', 'rb'))
    label_sequences_entire = pickle.load(open('training_labels.pkl', 'rb'))

# Train the model
log_err('Training samples retrieved!')
classifier = HMMClassifier()
log_err('Start Training...')
classifier.HMMentire.train(observation_sequences_entire, label_sequences_entire)
classifier.HMMentire.print_model()

